import { Component, OnInit } from '@angular/core';
import { Course } from '../../../models/course.model';
import { DanceService } from '../../../services/dance.service';
import { DanceSchool } from '../../../models/dance-school.model';


@Component({
  selector: 'app-course-list',
  templateUrl: './course-list.component.html',
  styleUrls: ['./course-list.component.css']
})
export class CourseListComponent implements OnInit {
  courses: Course[] = [];

  constructor(private danceService: DanceService) {}

  ngOnInit(): void {
    this.loadCourses();
  }

  loadCourses(): void {
    this.danceService.getAllCourses().subscribe(
      (data) => {
        this.courses = data;
      },
      (error) => {
        console.error('Erreur lors du chargement des cours', error);
      }
    );
  }

  deleteCourse(id: number): void {
    if (confirm('Êtes-vous sûr de vouloir supprimer ce cours ?')) {
      this.danceService.deleteCourse(id).subscribe(
        () => {
          this.loadCourses(); // Recharge la liste après la suppression
        },
        (error) => {
          console.error('Erreur lors de la suppression du cours', error);
        }
      );
    }
  }
}
