import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { DanceCategory } from '../../../models/dance-category.model';
import { DanceSchool } from '../../../models/dance-school.model';
import { DanceService } from '../../../services/dance.service';

@Component({
  selector: 'app-school-post',
  templateUrl: './school-post.component.html',
  styleUrls: ['./school-post.component.css']
})
export class SchoolPostComponent implements OnInit {
  schoolForm: FormGroup;
  danceCategories: DanceCategory[] = []; // Liste des catégories disponibles

  constructor(
    private fb: FormBuilder,
    private danceService: DanceService,
    private router: Router
  ) {
    // Initialisation du formulaire avec un champ danceCategory
    this.schoolForm = this.fb.group({
      name: ['', Validators.required],
      localisation: ['', Validators.required],
      openday: ['', Validators.required],
      closeday: ['', Validators.required],
      opentime: ['', Validators.required],
      closetime: ['', Validators.required],
      danceCategoryId: [null, Validators.required] // ID de la catégorie sélectionnée
    });
  }

  ngOnInit(): void {
    // Récupérer les catégories de danse disponibles
    this.danceService.getAllDanceCategories().subscribe({
      next: (categories) => {
        this.danceCategories = categories;
      },
      error: (err) => {
        console.error('Erreur lors de la récupération des catégories', err);
      }
    });
  }

  onSubmit(): void {
    if (this.schoolForm.valid) {
      // Construire l'objet DanceSchool avec la structure imbriquée
      const formValues = this.schoolForm.value;
      const newSchool: DanceSchool = {
        name: formValues.name,
        localisation: formValues.localisation,
        openday: formValues.openday,
        closeday: formValues.closeday,
        opentime: formValues.opentime,
        closetime: formValues.closetime,
        danceCategory: { id: formValues.danceCategoryId } // Objet imbriqué
      };

      // Envoi au backend via le service
      this.danceService.createDanceSchool(newSchool).subscribe({
        next: () => {
          this.router.navigate(['crud/school-list']); // Redirection vers la liste des écoles
        },
        error: (err) => {
          console.error('Erreur lors de la création de l\'école', err);
        }
      });
    } else {
      console.error('Le formulaire n\'est pas valide');
    }
  }
}
<form [formGroup]="schoolForm" (ngSubmit)="onSubmit()">
  <div>
    <label for="name">Nom de l'école</label>
    <input id="name" formControlName="name" />
  </div>
  <div>
    <label for="localisation">Localisation</label>
    <input id="localisation" formControlName="localisation" />
  </div>
  <div>
    <label for="openday">Jour d'ouverture</label>
    <input id="openday" formControlName="openday" />
  </div>
  <div>
    <label for="closeday">Jour de fermeture</label>
    <input id="closeday" formControlName="closeday" />
  </div>
  <div>
    <label for="opentime">Heure d'ouverture</label>
    <input id="opentime" formControlName="opentime" />
  </div>
  <div>
    <label for="closetime">Heure de fermeture</label>
    <input id="closetime" formControlName="closetime" />
  </div>

  <!-- Sélection de la catégorie de danse -->
  <div>
    <label for="danceCategoryId">Catégorie de danse</label>
    <select id="danceCategoryId" formControlName="danceCategoryId">
      <option [value]="null" disabled selected>Choisir une catégorie</option>
      <option *ngFor="let category of danceCategories" [value]="category.id">
        {{ category.name }}
      </option>
    </select>
  </div>

  <button type="submit" [disabled]="schoolForm.invalid">Créer l'école</button>
</form>
