import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { DanceSchool } from '../../../models/dance-school.model';
import { DanceService } from '../../../services/dance.service';

@Component({
  selector: 'app-school-post',
  templateUrl: './school-post.component.html',
  styleUrls: ['./school-post.component.css']
})
export class SchoolPostComponent implements OnInit {
  schoolForm: FormGroup;

  constructor(
    private fb: FormBuilder,
    private danceService: DanceService,
    private router: Router
  ) {
    this.schoolForm = this.fb.group({
      name: ['', Validators.required],
      localisation: ['', Validators.required],
      openday: ['', Validators.required],
      closeday: ['', Validators.required],
      opentime: ['', Validators.required],
      closetime: ['', Validators.required],
      danceCategoryId: ['', Validators.required]
    });
  }

  ngOnInit(): void {}

  onSubmit(): void {
    if (this.schoolForm.valid) {
      const school: DanceSchool = this.schoolForm.value;
      this.danceService.createDanceSchool(school).subscribe({
        next: () => {
          this.router.navigate(['crud/school-list']);
        },
        error: (err) => {
          console.error('Erreur lors de l\'ajout de l\'école', err);
        }
      });
    } else {
      console.error('Le formulaire n\'est pas valide');
    }
  }
}
