import { Component, OnInit } from '@angular/core';
import { Course } from '../../../models/course.model';
import { DanceService } from '../../../services/dance.service';
import { DanceSchool } from '../../../models/dance-school.model';

@Component({
  selector: 'app-course-list',
  templateUrl: './course-list.component.html',
  styleUrls: ['./course-list.component.css']
})
export class CourseListComponent implements OnInit {
  courses: Course[] = [];
  danceSchools: DanceSchool[] = []; // Liste des écoles de danse

  constructor(private danceService: DanceService) {}

  ngOnInit(): void {
    this.loadCourses();
    this.loadDanceSchools(); // Charger les écoles de danse pour afficher leurs noms
  }

  loadCourses(): void {
    this.danceService.getAllCourses().subscribe({
      next: (data) => {
        this.courses = data;
      },
      error: (err) => {
        console.error('Erreur lors de la récupération des cours', err);
      }
    });
  }

  loadDanceSchools(): void {
    this.danceService.getAllDanceSchools().subscribe({
      next: (data) => {
        this.danceSchools = data; // Remplir la liste des écoles de danse
      },
      error: (err) => {
        console.error('Erreur lors de la récupération des écoles de danse', err);
      }
    });
  }

  deleteCourse(id: number): void {
    this.danceService.deleteCourse(id).subscribe({
      next: () => {
        this.loadCourses(); // Recharger la liste après la suppression
      },
      error: (err) => {
        console.error('Erreur lors de la suppression du cours', err);
      }
    });
  }

  getDanceSchoolName(schoolId: number): string {
    const school = this.danceSchools.find(s => s.id === schoolId);
    return school ? school.name : 'Inconnu';
  }
}
